package com.wanandroid.zhangtianzhu.surveyinginstrumentsdemo.adapter;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.recyclerview.widget.RecyclerView;import java.util.List;/** * @param <T> * @author ztz * 通用的RecyclerView Adapter */public abstract class BaseRecyclerViewAdapter<T> extends RecyclerView.Adapter<BaseViewHolder> {    private Context mContext;    private LayoutInflater mInflater;    private List<T> mData;    private int mLayoutId;    public BaseRecyclerViewAdapter(Context mContext, List<T> mData, int layoutId) {        this.mContext = mContext;        this.mInflater = LayoutInflater.from(mContext);        this.mData = mData;        mLayoutId = layoutId;    }    @Override    public BaseViewHolder onCreateViewHolder(ViewGroup viewGroup, int i) {        View itemView = mInflater.inflate(mLayoutId, viewGroup, false);        BaseViewHolder viewHolder = new BaseViewHolder(itemView);        return viewHolder;    }    @Override    public void onBindViewHolder(BaseViewHolder baseViewHolder, int position) {        covert(baseViewHolder, mData.get(position), position);    }    /**     * 利用抽象方法回传数据     *     * @param holder     * @param item     当前位置数据     * @param position     */    public abstract void covert(BaseViewHolder holder, T item, int position);    @Override    public int getItemCount() {        return mData.size();    }    @Override    public int getItemViewType(int position) {        return position;    }}